  </header>
</template>

<script>
import strapiService from '../../services/strapi'

export default {
  name: 'HeaderSection',
  data() {
    return {
      isAtTop: true,
      isInBibliografia: false,
      isInBibliotecas: false,
      isMobileMenuOpen: false,
      isLibrosDigitales: false,
      showServiciosDropdown: false,
      showBibliografiaDropdown: false,
      showBibliotecasDropdown: false,
      serviciosTimeout: null,
      bibliografiaTimeout: null,
      bibliotecasTimeout: null,
      expandedBibliotecaAccordions: {
        norte: false,
        centro: false,
        metropolitana: false,
        sur: false
      },
      expandedSections: {
        servicios: false,
        bibliografia: true,
        bibliotecas: false
      },
      expandedSubsections: {
        norte: false,
        centro: false,
        metropolitana: false,
        sur: false
      },
      libraries: [],
      regions: [
        { id: 'norte', name: 'Zona Norte' },
        { id: 'centro', name: 'Zona Centro' },
        { id: 'metropolitana', name: 'Región Metropolitana' },
        { id: 'sur', name: 'Zona Sur' }
      ],
      schools: [
        { id: 1, name: 'Administración y Gestión Empresarial', searchName: 'Administración y Gestión Empresarial' },
        { id: 2, name: 'Artes e Industrias Creativa', searchName: 'Artes e Industrias Creativas' },
        { id: 3, name: 'Desarrollo Social y Educación', searchName: 'Desarrollo Social y Educación' },
        { id: 4, name: 'Estética Integral', searchName: 'Estética Integral' },
        { id: 5, name: 'Gastronomía, Hotelería y Turismo', searchName: 'Gastronomía y Hotelería' },
        { id: 6, name: 'Ingeniería, Energía y Tecnología', searchName: 'Ingeniería y Tecnología' },
        { id: 7, name: 'Salud y Deporte', searchName: 'Salud y Ciencias Médicas' }
      ]
    }
  },
  async mounted() {
    this.handleScroll()
    window.addEventListener('scroll', this.handleScroll)
    await this.loadLibraries()
    this.$nextTick(() => {
      this.checkLibrosDigitales()
      this.observeHeroSection()
    })
  },
  beforeUnmount() {
    window.removeEventListener('scroll', this.handleScroll)
    document.body.style.overflow = ''
  },
  methods: {
    handleScroll() {
      const scrollPosition = window.scrollY || window.pageYOffset
      const headerHeight = 80
      const threshold = 200 // Umbral para considerar que una sección está activa
      
      // Si está cerca del inicio (menos de 200px desde arriba), marcar Servicios como activo
      if (scrollPosition < 200) {
        this.isAtTop = true
        this.isInBibliografia = false
        this.isInBibliotecas = false
        return
      }
      
      this.isAtTop = false
      
      // Obtener posiciones de ambas secciones
      const bibliografiaSection = document.getElementById('bibliografia')
      const bibliotecasSection = document.getElementById('bibliotecas')
      
      let bibliografiaTop = null
      let bibliotecasTop = null
      
      if (bibliografiaSection) {
        bibliografiaTop = bibliografiaSection.getBoundingClientRect().top
      }
      if (bibliotecasSection) {
        bibliotecasTop = bibliotecasSection.getBoundingClientRect().top
      }
      
      // Determinar qué sección está activa basándose en cuál está más cerca del top del viewport
      // La sección activa es la que tiene su inicio más cerca del header
      if (bibliotecasTop !== null && bibliotecasTop <= headerHeight + threshold) {
        // Si Bibliotecas está cerca del header (dentro del umbral), activarla
        this.isInBibliotecas = true
        this.isInBibliografia = false
      } else if (bibliografiaTop !== null && bibliografiaTop <= headerHeight + threshold) {
        // Si Bibliografía está cerca del header (dentro del umbral), activarla
        this.isInBibliografia = true
        this.isInBibliotecas = false
      } else {
        // Si ninguna está cerca del header, verificar cuál está más visible
        if (bibliotecasTop !== null && bibliotecasTop < window.innerHeight && bibliotecasTop > -window.innerHeight) {
          this.isInBibliotecas = true
          this.isInBibliografia = false
        } else if (bibliografiaTop !== null && bibliografiaTop < window.innerHeight && bibliografiaTop > -window.innerHeight) {
          this.isInBibliografia = true
          this.isInBibliotecas = false
        } else {
          // Si ninguna está visible, mantener estados actuales
        }
      }
    },
    scrollToSection(hash, e) {
      if (e && e.preventDefault) {
      e.preventDefault()
      }
      const section = document.querySelector(hash)
      if (section) {
        const headerHeight = 80
        const sectionPosition = section.getBoundingClientRect().top + window.pageYOffset - headerHeight
        
        // Actualizar el estado inmediatamente basado en el hash
        if (hash === '#bibliografia') {
          this.isInBibliografia = true
          this.isInBibliotecas = false
          this.isAtTop = false
        } else if (hash === '#bibliotecas') {
          this.isInBibliotecas = true
          this.isInBibliografia = false
          this.isAtTop = false
        }
        
        window.scrollTo({
          top: sectionPosition,
          behavior: 'smooth'
        })
        // Actualizar el estado después de un pequeño delay para que la detección automática funcione
        setTimeout(() => {
          this.handleScroll()
        }, 500)
      }
    },
    scrollToTop(e) {
      e.preventDefault()
      window.scrollTo({
        top: 0,
        behavior: 'smooth'
      })
      this.isAtTop = true
    },
    toggleServiciosDropdown() {
      this.showServiciosDropdown = !this.showServiciosDropdown
    },
    showServiciosDropdownHover() {
      if (this.serviciosTimeout) {
        clearTimeout(this.serviciosTimeout)
        this.serviciosTimeout = null
      }
      this.showServiciosDropdown = true
    },
    hideServiciosDropdown() {
      if (this.serviciosTimeout) {
        clearTimeout(this.serviciosTimeout)
        this.serviciosTimeout = null
      }
      this.showServiciosDropdown = false
    },
    toggleBibliografiaDropdown() {
      this.showBibliografiaDropdown = !this.showBibliografiaDropdown
    },
    showBibliografiaDropdownHover() {
      if (this.bibliografiaTimeout) {
        clearTimeout(this.bibliografiaTimeout)
        this.bibliografiaTimeout = null
      }
      this.showBibliografiaDropdown = true
    },
    hideBibliografiaDropdown() {
      if (this.bibliografiaTimeout) {
        clearTimeout(this.bibliografiaTimeout)
        this.bibliografiaTimeout = null
      }
      this.showBibliografiaDropdown = false
    },
    toggleBibliotecasDropdown() {
      this.showBibliotecasDropdown = !this.showBibliotecasDropdown
    },
    showBibliotecasDropdownHover() {
      if (this.bibliotecasTimeout) {
        clearTimeout(this.bibliotecasTimeout)
        this.bibliotecasTimeout = null
      }
      this.showBibliotecasDropdown = true
    },
    hideBibliotecasDropdown() {
      if (this.bibliotecasTimeout) {
        clearTimeout(this.bibliotecasTimeout)
        this.bibliotecasTimeout = null
      }
      this.showBibliotecasDropdown = false
    },
    toggleBibliotecaAccordion(regionId) {
      this.expandedBibliotecaAccordions[regionId] = !this.expandedBibliotecaAccordions[regionId]
    },
    navigateToLibraryFromDropdown(library) {
      this.hideBibliotecasDropdown()
      this.navigateToLibrary(library)
    },
    toggleMobileMenu() {
      this.isMobileMenuOpen = !this.isMobileMenuOpen
      if (this.isMobileMenuOpen) {
        document.body.style.overflow = 'hidden'
      } else {
        document.body.style.overflow = ''
      }
    },
    closeMobileMenu() {
      this.isMobileMenuOpen = false
      document.body.style.overflow = ''
    },
    toggleSection(section) {
      this.expandedSections[section] = !this.expandedSections[section]
    },
    toggleSubsection(subsectionId) {
      this.expandedSubsections[subsectionId] = !this.expandedSubsections[subsectionId]
    },
    getLibrariesByRegion(regionId) {
      return this.libraries.filter(lib => lib.region === regionId)
    },
    async loadLibraries() {
      try {
        const response = await strapiService.getCollection('bibliotecas', {
          populate: '*'
        })
        this.libraries = response.data || []
      } catch (error) {
        console.error('Error loading libraries:', error)
        // Datos de ejemplo si falla la carga - mismos datos que BibliotecasSection
        this.libraries = [
          // Región Metropolitana
          {
            id: 1,
            name: 'Biblioteca Grajales',
            region: 'metropolitana'
          },
          {
            id: 2,
            name: 'Biblioteca Ejército',
            region: 'metropolitana'
          },
          {
            id: 3,
            name: 'Biblioteca San Joaquín',
            region: 'metropolitana'
          },
          // Zona Norte
          {
            id: 4,
            name: 'Biblioteca Antofagasta',
            region: 'norte'
          },
          {
            id: 5,
            name: 'Biblioteca La Serena',
            region: 'norte'
          },
          {
            id: 6,
            name: 'Biblioteca Iquique',
            region: 'norte'
          },
          {
            id: 13,
            name: 'Biblioteca Arica',
            region: 'norte'
          },
          {
            id: 14,
            name: 'Biblioteca Copiapó',
            region: 'norte'
          },
          {
            id: 15,
            name: 'Biblioteca Calama',
            region: 'norte'
          },
          {
            id: 16,
            name: 'Biblioteca Coquimbo',
            region: 'norte'
          },
          // Zona Centro
          {
            id: 7,
            name: 'Biblioteca Valparaíso',
            region: 'centro'
          },
          {
            id: 8,
            name: 'Biblioteca Viña del Mar',
            region: 'centro'
          },
          {
            id: 9,
            name: 'Biblioteca Rancagua',
            region: 'centro'
          },
          // Zona Sur
          {
            id: 10,
            name: 'Biblioteca Concepción',
            region: 'sur'
          },
          {
            id: 11,
            name: 'Biblioteca Temuco',
            region: 'sur'
          },
          {
            id: 12,
            name: 'Biblioteca Valdivia',
            region: 'sur'
          }
        ]
      }
    },
    navigateToLibrary(library) {
      this.closeMobileMenu()
      // Navegar a la sección de bibliotecas
      this.scrollToSection('#bibliotecas', { preventDefault: () => {} })
      
      // Esperar a que se complete el scroll y luego buscar la tarjeta de la biblioteca
      setTimeout(() => {
        const bibliotecasSection = document.getElementById('bibliotecas')
        if (bibliotecasSection) {
          // Primero, seleccionar la región correspondiente si hay tabs
          const regionTabs = bibliotecasSection.querySelectorAll('.region-tab')
          regionTabs.forEach(tab => {
            const tabText = tab.textContent.trim()
            const regionName = this.regions.find(r => r.id === library.region)?.name
            if (tabText === regionName) {
              tab.click()
            }
          })
          
          // Esperar un poco más para que se actualice el contenido con la región seleccionada
          setTimeout(() => {
            const libraryCards = bibliotecasSection.querySelectorAll('.library-card')
            
            // Buscar la tarjeta de la biblioteca
            libraryCards.forEach(card => {
              const nameElement = card.querySelector('.library-name')
              if (nameElement) {
                const cardName = nameElement.textContent.trim()
                if (cardName === library.name) {
                  // Hacer scroll hasta la tarjeta
                  card.scrollIntoView({ behavior: 'smooth', block: 'center' })
                  // Resaltar la tarjeta brevemente
                  card.style.transition = 'box-shadow 0.3s'
                  card.style.boxShadow = '0 0 20px rgba(36, 51, 78, 0.5)'
                  setTimeout(() => {
                    card.style.boxShadow = ''
                  }, 2000)
                }
              }
            })
          }, 300)
        }
      }, 600)
    },
    checkLibrosDigitales() {
      // Esperar un poco para asegurar que el DOM esté actualizado
      setTimeout(() => {
        const digitalesButton = document.querySelector('.hero .tab.active')
        if (digitalesButton) {
          const buttonText = digitalesButton.textContent.trim()
          this.isLibrosDigitales = buttonText === 'Libros digitales'
        } else {
          this.isLibrosDigitales = false
        }
      }, 50)
    },
    observeHeroSection() {
      // Verificar periódicamente si el HeroSection está disponible
      const checkInterval = setInterval(() => {
        const heroSection = document.querySelector('.hero .widget-tabs')
        if (heroSection) {
          clearInterval(checkInterval)
          
          // Observar cambios en los tabs del HeroSection
          const observer = new MutationObserver(() => {
            this.checkLibrosDigitales()
          })
          
          observer.observe(heroSection, {
            childList: true,
            subtree: true,
            attributes: true,
            attributeFilter: ['class']
          })
          
          // También escuchar clicks en los tabs
          const tabs = document.querySelectorAll('.hero .tab')
          tabs.forEach(tab => {
            tab.addEventListener('click', () => {
              setTimeout(() => {
                this.checkLibrosDigitales()
              }, 100)
            })
          })
          
          // Verificación inicial
          this.checkLibrosDigitales()
        }
      }, 100)
      
      // Limpiar el intervalo después de 5 segundos si no se encuentra
      setTimeout(() => {
        clearInterval(checkInterval)
      }, 5000)
    },
    navigateToSchool(school) {
      this.closeMobileMenu()
      // Navegar a la sección de bibliografía
      this.scrollToSection('#bibliografia', { preventDefault: () => {} })
      
      // Esperar a que se complete el scroll y luego buscar la tarjeta de la escuela
      setTimeout(() => {
        const bibliografiaSection = document.getElementById('bibliografia')
        if (bibliografiaSection) {
          // Buscar todas las tarjetas de escuelas
          const schoolCards = bibliografiaSection.querySelectorAll('.school-card')
          schoolCards.forEach(card => {
            const titleElement = card.querySelector('.card-title')
            if (titleElement) {
              const cardName = titleElement.textContent.trim()
              // Buscar por nombre exacto o por nombre de búsqueda
              if (cardName === school.searchName || cardName === school.name) {
                // Hacer scroll hasta la tarjeta
                card.scrollIntoView({ behavior: 'smooth', block: 'center' })
                // Resaltar la tarjeta brevemente
                card.style.transition = 'box-shadow 0.3s'
                card.style.boxShadow = '0 0 20px rgba(36, 51, 78, 0.5)'
                setTimeout(() => {
                  card.style.boxShadow = ''
                }, 2000)
              }
            }
          })
        }
      }, 600)
    }
  }
}
</script>

